classdef tSmokeFigureDeployerDoc < matlab.unittest.TestCase        properties (TestParameter)        DocFile = {'FigureDeployerDoc'};    end        properties        figs = findall(0,'type','figure');    end    methods (TestClassSetup)        % Shut off graphics warnings        function killGraphicsWarning(testCase)            ws = warning('off', 'MATLAB:hg:AutoSoftwareOpenGL');            testCase.addTeardown(@()warning(ws));        end    end        methods (Test)        function demosShouldNotWarn(testCase, DocFile)                   DemoFileFcn = str2func(DocFile); % Use str2func() instead of @ because test names it the string            testCase.verifyWarningFree(DemoFileFcn);                    end    end        methods (TestClassTeardown)        function deleteNewPlots(testCase)            % Compare all figures against the figures open before testing            % to delete the ones generated from running a unit test.            newfigs = findall(0,'type','figure');            delete(setdiff(newfigs,testCase.figs))        end    endend